function addtable(c){var a=document.getElementById(c),b=a.id.replace("td-","");if(a.id.indexOf("td-")==0){document.getElementById(c).innerHTML='<table id="tctrl-'+b+'"></table';document.getElementById(c).onclick=donothing;addrow("tctrl-"+b);addcolumn("tctrl-"+b)}else{document.getElementById(c).innerHTML='<table id="tctrl-"></table';addrow("tctrl-");addcolumn("tctrl-")}}function filtertctrl(a){return a+"tctrl-".replace("tctrl-","")}function donothing(){}function createCell(a,c,b){a.innerHTML=c;a.id=c;a.onclick=insertInInput}function insertInInput(){document.getElementById("atable").value=this.id;document.getElementById("arow").value=this.parentNode.parentNode.parentNode.id;document.getElementById("acolumn").value=this.parentNode.parentNode.parentNode.id}function addrow(f){var c=document.getElementById(f),d=c.insertRow(c.rows.length),e="",a,b;if(c.parentNode.id.indexOf("td-")==0){e=c.parentNode.id.replace("td-","")+"-"}for(a=0;a<c.rows[0].cells.length;a++){b=c.rows.length-1;createCell(d.insertCell(a),"td-"+e+a+"-"+b,"row")}}function addcolumn(e){var c=document.getElementById(e),d="",a,b;if(c.parentNode.id.indexOf("td-")==0){d=c.parentNode.id.replace("td-","")+"-"}for(a=0;a<c.rows.length;a++){b=c.rows[a].cells.length;createCell(c.rows[a].insertCell(c.rows[a].cells.length),"td-"+d+b+"-"+a,"col")}}function getinfo(b){var a=document.getElementById(b);alert("Name: "+a.nodeName+"\nTop: "+a.offsetTop+"\nLeft: "+a.offsetLeft+"\nHeight: "+a.offsetHeight+"\nWidth: "+a.offsetWidth+"\n")}function buildtablefromcsv(b,k){var m,l,d,e,c,f,h,g;var a="";d=document.getElementById(b).innerHTML;m=d.split("\n");f="0";for(e=0;e<m.length;e++){if(m[e]==""){f=""}else{l=m[e].split(k);if(l[0]=="tablectrl"){g=l[2];if(l[2]==undefined){g=l[1].split("td-");g="tctrl-"+g[1]}f='<table id="'+g+'">\n'+f+"</table>\n";document.getElementById(l[1]).innerHTML=f}else{f+="<tr>";for(c=0;c<l.length;c++){f+='<td id="'+l[c]+'">'+l[c]+"</td>"}f+="</tr>\n"}}}};